    public function LoadLoginForm()
    {
        $this->http->removeCookies();

        if (filter_var($this->AccountFields['Login'], FILTER_VALIDATE_EMAIL) === false) {
            throw new CheckException('The username or password provided are invalid. If you have not created an account for this website please do so first, otherwise verify your entry and try again.', ACCOUNT_INVALID_PASSWORD);
        }

        $this->http->RetryCount = 0;
        $this->http->GetURL("https://club.hiltongrandvacations.com/en/login#/index");
        $this->http->RetryCount = 2;
        // Cloudflare workaround
        $this->challengeReCaptchaForm();
        // Checking your browser before accessing
        $this->challengeForm();

        if ($this->http->Response['code'] == 403 || empty($this->http->Response['body'])) {
            $this->DebugInfo = 'blocked by Cloudflare';
            $this->ErrorReason = self::ERROR_REASON_BLOCK;

            throw new CheckRetryNeededException(3, 0);
        } else {
            $this->DebugInfo = null;
            $this->ErrorReason = null;
        }

        $postData = $this->http->FindPreg("/hgv\[\'Login\'\]\s*=\s*new\s*hgv\[\'Login\'\]\((.+)\)\;\s*\}\s*\(window\[window\.appnamespace\]/ims");

        $sensorDataUrl = $this->http->FindPreg("# src=\"([^\"]+)\"><\/script><#");

        if (!$sensorDataUrl) {
            $this->logger->error("sensor_data URL not found");

            return $this->checkErrors();
        }

        if (!$postData) {
            return $this->checkErrors();
        }
//        $postData = $this->http->JsonLog($postData, false);
        $postData = str_replace('"EmailAddress": null, "Password": null', '"EmailAddress": "' . $this->AccountFields['Login'] . '", "Password": "' . $this->AccountFields['Pass'] . '"', $postData);

        if (strstr($postData, ', "RecaptchaRequired": true,') && strstr($postData, ', "RecaptchaVerifyValue": null,')) {
            $key = $this->http->FindSingleNode('//form[@class="pt-l"]//div[@class = "g-recaptcha"]/@data-sitekey');
            $captcha = $this->parseCaptcha($key);

            if ($captcha === false) {
                return false;
            }
            $postData = str_replace(', "RecaptchaVerifyValue": null,', ', "RecaptchaVerifyValue": "' . $captcha . '",', $postData);
        }

        $this->http->NormalizeURL($sensorDataUrl);
        /*
        $this->sendSensorData($sensorDataUrl);
        */
        $abck = [
            // 0
            "1A8984A32EE57ACB79539875E85795C0~0~~-1~-1~-1",
            // 1
            "1A8984A32EE57ACB79539875E85795C0~0~~-1~-1~-1",
            // 2
            "161E383C599F80BC44353FFB95E1300F~0~~-1~-1~-1",
            // 3
            "7943242B380782C4FE639E75412848F7~0~~-1~-1~-1",
            // 4
            "6E71B28E2D0A42163D7B4FA59ECAA65D~0~~-1~-1~-1",
            // 5
            "220B5D1233D97052567C19BF46FEADF5~0~~-1~-1~-1",
            // 6
            "BD7332EFEDAE1C222EBAEDEA2E796452~0~~-1~-1~-1",
            // 7
            "DC9D62A371F81F4D6C63A90C6534DA35~0~~-1~-1~-1",
            // 8
            "69D2193AC6D2797F2061FEE4FDA79D5E~0~~-1~-1~-1",
        ];
        $key = array_rand($abck);
        $this->logger->notice("key: {$key}");
        $this->DebugInfo = "key: {$key}";
        $this->http->setCookie("_abck", $abck[$key], ".hiltongrandvacations.com"); // todo: sensor_data workaround

//        $this->logger->debug(var_export($postData, true), ['pre' => true]);
        $headers = [
            "Accept"           => "*/*",
            "Content-Type"     => "application/json;charset=utf-8",
            "X-Requested-With" => "XMLHttpRequest",
        ];
        $this->http->RetryCount = 0;
        $this->http->PostURL("https://club.hiltongrandvacations.com/en/login/Authenticate", $postData, $headers);
        $this->http->RetryCount = 2;

        return true;
    }

    public function checkErrors()
    {
        $this->logger->notice(__METHOD__);
        // maintenance
        if ($this->http->FindSingleNode("//p[contains(text(), 'Our website and mobile app are currently undergoing maintenance')]")) {
            throw new CheckException('Our website and mobile app are currently undergoing maintenance', ACCOUNT_PROVIDER_ERROR);
        }

        if ($this->http->FindSingleNode("//p[contains(text(), 'Our website and mobile app are currently unavailable')]")) {
            throw new CheckException('Our website and mobile app are currently unavailable', ACCOUNT_PROVIDER_ERROR);
        }

        if ($message = $this->http->FindSingleNode("//p[contains(., ', the Hilton Grand Vacations Member website, mobile app and system are temporarily unavailable due to maintenance')]")) {
            throw new CheckException($message, ACCOUNT_PROVIDER_ERROR);
        }

        if ($this->http->Response['code'] == 404
            && $this->http->FindSingleNode("//h2[contains(text(), '404 - File or directory not found.')]")) {
            throw new CheckException(self::PROVIDER_ERROR_MSG, ACCOUNT_PROVIDER_ERROR);
        }

        return false;
    }

    public function Login()
    {
        // invalid credentials
        if (
            $this->http->Response['code'] == 400
            && $this->http->FindPreg("/\"ReturnUrl\":null/")
        ) {
            if (
                $this->http->FindPreg("/\"RecaptchaRequired\":true,\"RecaptchaVerifyValue\":null,\"RecaptchaNotValidError\":true,/")
                || $this->http->FindPreg("/\"RecaptchaRequired\":true,\"TwoFactorAuthRequired\":false,\"TwoFactorStep\":1,\"TwoFactorElect\":false,\"TwoFactorAuthRequiresSetup\":false,\"TwoFactorAuthCode\":null,\"TwoFactorPhoneNumber\":null,\"RecaptchaVerifyValue\":null,\"RecaptchaNotValidError\":true,/")
            ) {
                $this->captchaReporting($this->recognizer, false);

                throw new CheckRetryNeededException(3, 0, self::CAPTCHA_ERROR_MSG);
            }

            $this->captchaReporting($this->recognizer);

            if (
                $this->http->FindPreg("/\"RecaptchaRequired\":false,\"RecaptchaVerifyValue\":null,\"RecaptchaNotValidError\":false/")
                || $this->http->FindPreg("/\"RecaptchaRequired\":true,\"RecaptchaVerifyValue\":null,\"RecaptchaNotValidError\":false/")
                || $this->http->FindPreg("/\"RecaptchaRequired\":true,\"TwoFactorAuthRequired\":false,\"TwoFactorStep\":1,\"TwoFactorElect\":false,\"TwoFactorAuthRequiresSetup\":false,\"TwoFactorAuthCode\":null,\"TwoFactorPhoneNumber\":null,\"RecaptchaVerifyValue\":null,\"RecaptchaNotValidError\":false,/")
                || $this->http->FindPreg("/\"RecaptchaRequired\":false,\"TwoFactorAuthRequired\":false,\"TwoFactorStep\":1,\"TwoFactorElect\":false,\"TwoFactorAuthRequiresSetup\":false,\"TwoFactorAuthCode\":null,\"TwoFactorPhoneNumber\":null,\"RecaptchaVerifyValue\":null,\"RecaptchaNotValidError\":false,\"IsLockedOut\":false,\"IncorrectEmailOrPasswordError\":true,\"ContractOnlyError\":false,\"NoActiveMembershipsError\":false,\"IsAdmin\":false,\"VoiceUsername\":null,\"UpdateEmailToken\":null,\"TwoFactorServiceIssue\":false,\"ClientId\":null/")
            ) {
                throw new CheckException("The username or password provided are invalid. If you have not created an account for this website please do so first, otherwise verify your entry and try again.", ACCOUNT_INVALID_PASSWORD);
            }

            if ($this->http->FindPreg('/"RecaptchaRequired":false,"TwoFactorAuthRequired":false,"TwoFactorStep":1,"TwoFactorElect":false,"TwoFactorAuthRequiresSetup":false,"TwoFactorAuthCode":null,"TwoFactorPhoneNumber":null,"RecaptchaVerifyValue":null,"RecaptchaNotValidError":false,"IsLockedOut":false,"IncorrectEmailOrPasswordError":false,"ContractOnlyError":false,"NoActiveMembershipsError":true,"IsAdmin":false,"VoiceUsername":null,"UpdateEmailToken":null,"TwoFactorServiceIssue":false,"ClientId":null/')) {
                throw new CheckException("There are no active memberships associated with this account. Please contact us at 800-932-4482 (U.S. and Canada) or 407-613-3141 (Worldwide) if you believe this to be in error.", ACCOUNT_INVALID_PASSWORD);
            }

            // Two-Factor Authentication
            if ($this->http->FindPreg('/"RecaptchaRequired":false,"TwoFactorAuthRequired":true,"TwoFactorStep":1,"TwoFactorElect":false,"TwoFactorAuthRequiresSetup":false,"TwoFactorAuthCode":null,"TwoFactorPhoneNumber":null,"RecaptchaVerifyValue":null,"RecaptchaNotValidError":false,"IsLockedOut":false,"IncorrectEmailOrPasswordError":false,"ContractOnlyError":false,"NoActiveMembershipsError":false,"IsAdmin":false,"VoiceUsername":null,"UpdateEmailToken":null,"TwoFactorServiceIssue":false,"ClientId":null/')) {
                // Two Factor Auth Code
                $this->AskQuestion('Please enter the Two Factor Auth Code that has been sent to you', null, 'Question');

                return false;
            }

            return false;
        }

        // retries
        if ($this->http->Response['code'] == 520 && $this->http->FindSingleNode('//h2[contains(text(), "Web server is returning an unknown error")]')) {
            $this->captchaReporting($this->recognizer);

            throw new CheckRetryNeededException(3, 7);
        }

        if (
            $this->http->Response['code'] == 500
            && ($message = $this->http->FindSingleNode("//h1[contains(text(), 'Sorry, an error has occurred.')]"))
        ) {
            $this->captchaReporting($this->recognizer);

            throw new CheckException($message, ACCOUNT_PROVIDER_ERROR);
        }

        if ($this->http->Response['code'] == 403) {
            $this->DebugInfo = "need to upd sensor_data {$this->DebugInfo}";

            return false;
        }

        $this->http->GetURL("https://club.hiltongrandvacations.com/en/login/loginflow");

        // Until you receive a message confirming your membership activation, please contact your Client Relations Manager at the contact number provided to you on the day of purchase if you need assistance.
        if ($this->http->FindSingleNode("//p[contains(text(), 'Until you receive a message confirming your membership activation, please contact your')]")) {
            $this->captchaReporting($this->recognizer);

            throw new CheckException(self::NOT_MEMBER_MSG, ACCOUNT_PROVIDER_ERROR);
        }

        if (
            $this->http->FindNodes("//a[contains(@href, 'signout')]/@href")
            && !stristr($this->http->currentUrl(), 'https://club.hiltongrandvacations.com/en/select-membership')
        ) {
            $this->captchaReporting($this->recognizer);
            $this->Parse(false, null);

            return false;
        }

        // multiple accounts
        $this->logger->debug("[Current URL]: {$this->http->currentUrl()}");
        $user = $this->http->FindPreg("/\"FrontEndRoute\":\s*\"select-membership\",\s*\"User\":([^\}]+\})/");
//        $this->logger->debug(var_export($user, true), ['pre' => true]);
        if (stristr($this->http->currentUrl(), 'https://club.hiltongrandvacations.com/en/select-membership')
            && ($postData = $this->http->FindPreg("/new hgv\['SelectMembership'\]\(([\w\s\D]+)\)\;\s*\}\(window\[window\.appnamespace\]\s*=\s*window\[window\.appnamespace\]/"))) {
            $this->captchaReporting($this->recognizer);

            $this->DebugInfo = 'Multiple accounts';
            $this->logger->notice('Multiple accounts');
            $headers = [
                "Accept"           => "*/*",
                "Accept-Encoding"  => "gzip, deflate, br",
                "Content-Type"     => "application/json;charset=utf-8",
                "ADRUM"            => "isAjax:true",
                "X-Requested-With" => "XMLHttpRequest",
                "Referer"          => "https://club.hiltongrandvacations.com/en/select-membership",
                "Pragma"           => "no-cache",
                "Origin"           => "https://club.hiltongrandvacations.com",
            ];
            $this->http->GetURL("https://club.hiltongrandvacations.com/en/select-membership/Item?id=en&_=" . time() . date("B"));
            $response = $this->http->JsonLog(null, 1);
            $memberships = [];

            if (isset($response->ItemDetail->ClubMembershipRows[0]->Memberships)) {
                $memberships = $response->ItemDetail->ClubMembershipRows[0]->Memberships;
            }

            foreach ($memberships as $membership) {
                //$this->logger->error(json_encode($memberships));
                $postData = str_replace('"Memberships":[Memberships]', '"Memberships":' . json_encode($memberships), $this->postData);
                $postData = str_replace('"SelectedMembership":[SelectedMembership]', '"SelectedMembership":' . json_encode($membership), $postData);
                $postData = str_replace('"User":[User]', '"User":' . $user, $postData);
                $this->http->RetryCount = 1;
                $this->http->PostURL("https://club.hiltongrandvacations.com/en/select-membership/Select", $postData, $headers);
                $this->http->RetryCount = 2;
                $this->http->GetURL("https://club.hiltongrandvacations.com/en/login/loginflow");

                $memberNo = $this->http->FindPreg('/"MemberNo":\s*"([^\"]+)/ims');

                if (!$memberNo && $this->http->FindSingleNode("//h1[contains(text(),'Manage Ownership')]")) {
                    $this->logger->error('Skip account');
                } else {
                    $this->Parse(true, $membership->ClubType);
                }
            }

            return false;
        }

        return $this->checkErrors();
    }

    public function ProcessStep($step)
    {
        $this->postAuthData = str_replace('"EmailAddress":"[email]', '"EmailAddress":"' . $this->AccountFields['Login'], $this->postAuthData);
        $this->postAuthData = str_replace('"Password":"[pass]', '"Password":"' . $this->AccountFields['Login'], $this->postAuthData);
        $this->postAuthData = str_replace('"TwoFactorAuthCode":"[2auth]', '"TwoFactorAuthCode":"' . $this->Answers[$this->Question], $this->postAuthData);
        unset($this->Answers[$this->Question]);
        $headers = [
            "Accept"           => "*/*",
            "Accept-Encoding"  => "gzip, deflate, br",
            "Content-Type"     => "application/json;charset=utf-8",
            "ADRUM"            => "isAjax:true",
            "X-Requested-With" => "XMLHttpRequest",
            "Referer"          => "https://club.hiltongrandvacations.com/en/login",
            "Origin"           => "https://club.hiltongrandvacations.com",
        ];
        $this->http->RetryCount = 0;
        $this->http->PostURL("https://club.hiltongrandvacations.com/en/login/TwoFactor", $this->postAuthData, $headers, 120);
        $this->http->RetryCount = 2;
        $this->http->JsonLog(null, 2);
//        $this->sendNotification("Two Factor Auth Code // MI");

        if ($this->http->Response['code'] != 200) {
            return false;
        }

        $this->http->GetURL("https://club.hiltongrandvacations.com/en/login/loginflow");

        return true;
    }

