                    "CurrentTier"           => $this->http->FindPreg("/Current Tier:\s*([^<]+)/"),
                    //# As of
                    "AsOf"                  => $this->http->FindPreg("/Point Balance:\s*[\d\,\.\-\s]+\s*<i>\s*\(as\s*of\s*([^\)]+)/"),
                ]
            );
        }// if ($balance = $this->http->FindPreg("/Point Balance:\s*([\d\,\.\-\s]+)\s*<i>/"))
        */

        // AccountID: 4945971
        $noAccountNumber = false;

        if (
            $multipleAccounts === false
            && $this->http->FindSingleNode('
                //p[contains(text(), "Select a property to manage your loans, maintenance fees, and view HOA information")]
                | //h4[contains(text(), "SELECT AN OPTION BELOW TO MANAGE YOUR HILTON GRAND VACATIONS CLUB OWNERSHIP")]
            ')
        ) {
            $noAccountNumber = true;
        }

        // Current Points
        $this->http->RetryCount = 0;
        $this->http->GetURL("https://club.hiltongrandvacations.com/en/points-dashboard");
        $this->http->RetryCount = 2;
//        $this->http->GetURL("https://club.hiltongrandvacations.com/en/manage-points#/index");
        $result = $this->http->FindPreg("/hgv\[\'Points\'\]\s*=\s*new\s*hgv\[\'Points\'\]\((.+)\)\;\s*\}\s*\(window\[window\.appnamespace\]/ims");
        $result = $this->http->JsonLog($result, 0, true, 'ItemDetail');
        $itemDetail = ArrayVal($result, 'ItemDetail');
//        $this->logger->debug(var_export($itemDetail, true), ["pre" => true]);
        $pointsByYears = ArrayVal($itemDetail, 'PointsByYear', []);

        if (!empty($pointsByYears)) {
            $this->addSubAcc($pointsByYears, $multipleAccounts, $memberNo, $name, $memberSince, "ClubPoints", $clubType);
        } else {
            $pointsBuckets = ArrayVal($itemDetail, 'PointsBuckets', []);

            $clubPoints = ArrayVal($pointsBuckets, 'ClubPoints', []);
            $this->addSubAcc($clubPoints, $multipleAccounts, $memberNo, $name, $memberSince, "ClubPoints", $clubType);
            // refs #16291
            $bhcPoints = ArrayVal($pointsBuckets, 'BhcPoints', []);
            $this->addSubAcc($bhcPoints, $multipleAccounts, $memberNo, $name, $memberSince, "By Hilton Club Points", $clubType);

            // refs #16291
            $bonusPoints = ArrayVal($pointsBuckets, 'TotalBonusPoints', null);

            if ($bonusPoints) {
                $bonusPointsDetails = ArrayVal($pointsBuckets, 'BonusPointsDetails', []);
                $this->logger->debug("Total " . count($pointsByYears) . " nodes were found");
                $subAccType = 'Bonus Points';

                foreach ($bonusPointsDetails as $bonusPointsDetail) {
                    $bonusPointsRemaining = ArrayVal($bonusPointsDetail, 'BonusPointsRemaining', null);
                    $expirationDate = ArrayVal($bonusPointsDetail, 'ExpirationDate', null);

                    if (!isset($exp) || strtotime($expirationDate) < $exp) {
                        $expBalance = $bonusPointsRemaining;
                        $exp = strtotime($expirationDate);
                    }// if (!isset($exp) || strtotime($expirationDate) < $exp)
                }// foreach ($bonusPointsDetails as $bonusPointsDetail)

                if (isset($exp, $expBalance)) {
                    $this->AddSubAccount(
                        [
                            "Code"              => "hiltongvc" . str_replace(' ', '', $clubType) . str_replace(' ', '', $subAccType) . "#{$memberNo}",
                            "DisplayName"       => $multipleAccounts ? "{$clubType} Member #{$memberNo}: {$subAccType}" : "{$subAccType}",
                            "Balance"           => $bonusPoints,
                            "ExpirationDate"    => $exp,
                            "Name"              => $name,
                            "Member"            => $memberNo,
                            "MemberSince"       => $memberSince,
                            "ExpiringBalance"   => $expBalance,
                            "BalanceInTotalSum" => true,
                        ]
                    );

                    $this->balance += $bonusPoints;
                }// if (isset($exp, $expBalance))
            }// if ($bonusPoints)
        }

        if (isset($this->Properties['SubAccounts']) && count($this->Properties['SubAccounts']) > 0) {
            $this->SetProperty("CombineSubAccounts", false);
            $this->SetBalance($this->balance);
        }// if (isset($this->Properties['SubAccounts']) && count($this->Properties['SubAccounts']) > 0)
        // AccountID: 4945971 (and partial AccountID: 4087078)
        elseif (
            $this->ErrorCode == ACCOUNT_ENGINE_ERROR
            && $this->http->currentUrl() == 'https://club.hiltongrandvacations.com/en/error/ServerError'
            && $this->http->Response['code'] == 500
            && $noAccountNumber === true
            && empty($this->Properties['SubAccounts'])
            && empty($this->Properties['MemberSince'])
            && empty($this->Properties['Member'])
            && !empty($this->Properties['Name'])
        ) {
            $this->SetBalanceNA();

            $this->noItineraries = true;
        }

        if ($this->ParseIts) {
            $this->logger->notice('Parse Itineraries');
            $this->ParseItineraries();
        }
    }

    public function ParseItineraries()
    {
        $this->logger->notice(__METHOD__);

        if ($this->noItineraries) {
            $this->itinerariesMaster->setNoItineraries(true);

            return [];
        }

        $this->http->GetURL('https://club.hiltongrandvacations.com/api/Reservation?%24inlinecount=allpages&%24orderby=ArrivalDate+asc&%24top=100&%24skip=0&Type=0&ADAOnly=false&CalendarEnd=0001-01-01T00%3A00%3A00&CalendarStart=0001-01-01T00%3A00%3A00&CheckIn=&CheckOut=&NumberOfGuest=&OpenSeasonMode=false&ReservationNumber=&ResortId=&IsFixedDates=false&IsFlexAndCashMobileAvailability=false&CategoryName=&BookingType=&SearchTerm=&CurrentLanguageCode=en&DatabaseArea=0&LeadNumber=0&DirectoryFK=0&_=' . date("UB"));
        $response = $this->http->JsonLog(null, 0);

        if (!empty($response->List)) {
            $this->logger->debug("Found " . count($response->List) . " itineraries");

            foreach ($response->List as $itinerary) {
                $this->parseItinerary($itinerary, '');
            }
        }

        if ($this->ParsePastIts) {
            $this->http->GetURL('https://club.hiltongrandvacations.com/api/ReservationHistory?%24inlinecount=allpages&%24orderby=ArrivalDate+desc&%24top=50&%24skip=0&Type=0&ADAOnly=false&CalendarEnd=0001-01-01T00%3A00%3A00&CalendarStart=0001-01-01T00%3A00%3A00&CheckIn=&CheckOut=&NumberOfGuest=&OpenSeasonMode=false&ReservationNumber=&ResortId=&IsFixedDates=false&IsFlexAndCashMobileAvailability=false&CategoryName=&BookingType=&SearchTerm=&CurrentLanguageCode=en&DatabaseArea=0&LeadNumber=0&DirectoryFK=0&_=' . date("UB"));
            $response = $this->http->JsonLog(null, 0);

            if (!empty($response->List)) {
                $this->logger->debug("Found past " . count($response->List) . " itineraries");

                foreach ($response->List as $itinerary) {
                    $this->parseItinerary($itinerary, 'Past ');
                }
            }
        }

        // https://clubs.rci.com/
        $this->http->GetURL('https://club.hiltongrandvacations.com/RciExchangeLogin');
        $data = [];

        if ($this->http->ParseForm('form5')) {
            $data = [
                'clubId'        => $this->http->Form['clubId'],
                'clubMemberId'  => $this->http->Form['clubMemberId'],
                'email'         => $this->http->Form['emailAddress'],
                'referenceURL'  => !empty($this->http->Form['referenceURL']) ? $this->http->Form['referenceURL'] : null,
                'memberBalance' => $this->http->Form['memberBalance'],
                'leadId'        => null,
            ];
            $this->http->PostForm();
        }

        if (empty($data)) {
            return [];
        }

        $this->http->PostURL('https://services.b2c.rci.com/ext/v1/clubs/members/authentication?ConsumerChannel=Web&MemberType=POINTS&OperatorId=WEB00USER', json_encode($data));
        $userData = $this->http->JsonLog(null, 0);

        /*$this->logger->debug($this->http->getCookieByName('__bearerrciclubsapp__', 'clubs.rci.com'));
        $this->logger->debug(urldecode($this->http->getCookieByName('__bearerrciclubsapp__', 'clubs.rci.com')));

        $userData = $this->http->JsonLog(urldecode($this->http->getCookieByName('__bearerrciclubsapp__', 'clubs.rci.com')));
        */

        $leadId = $userData->customerInfo->profileInfo->leadId ?? null;

        if (isset($leadId)) {
            $this->http->GetURL("https://services.b2c.rci.com/ext/v1/clubs/{$leadId}/vacations/details?type=CONFIRMED&ConsumerChannel=Web&MemberType=POINTS&OperatorId=WEB00USER");
            $itData = $this->http->JsonLog(null, 0);
        }

        if (!isset($itData->details)) {
            return [];
        }

        foreach ($itData->details as $item) {
            $this->parseItineraryRci($item);
        }

        return [];
    }

    protected function parseCaptcha($key, $method = 'userrecaptcha')
    {
        $this->logger->notice(__METHOD__);
        $this->logger->debug("data-sitekey: {$key}");

        if (!$key) {
            return false;
        }
        $this->recognizer = $this->getCaptchaRecognizer(self::CAPTCHA_RECOGNIZER_RUCAPTCHA);
        $this->recognizer->RecognizeTimeout = 120;
        $this->logger->notice("currentUrl: " . $this->http->currentUrl());
        $parameters = [
            "method"  => $method,
            "pageurl" => $this->http->currentUrl(),
            "proxy"   => $this->http->GetProxy(),
        ];

        return $this->recognizeByRuCaptcha($this->recognizer, $key, $parameters);
    }

    private function parseItineraryRci($data)
    {
        $this->logger->notice(__METHOD__);
        $h = $this->itinerariesMaster->add()->hotel();
        $data->reservationNumber = $data->reservationNumber ?? null;
        $this->logger->info("Parse Itinerary Rci#{$data->reservationNumber}", ['Header' => 3]);

        $h->ota()->confirmation($data->resort->resortCode);
        $data->status = beautifulName($data->status);

        if (isset($data->reservationNumber)) {
            $h->general()->confirmation($data->reservationNumber, "Confirmation number");
        } else {
            $h->general()->noConfirmation();
        }
        $h->general()
            ->date2($data->confirmationDate)
            ->status(beautifulName($data->status));

        if ($data->status == 'Canceled') {
            $h->general()->cancelled();
        }

        $h->hotel()
            ->name($data->resort->resortName);

        $h->booked()
            ->checkIn(strtotime($data->resort->checkInTime, strtotime($data->checkInDate)))
            ->checkOut(strtotime($data->resort->checkOutTime, strtotime($data->checkOutDate)));

        if (isset($data->resort->contact->addresses[0])) {
            $address = $data->resort->contact->addresses[0];
            $state = $address->state ?? "";
            $postalCode = $address->postalCode ?? "";
            $line2 = !empty($address->line2) ? "$address->line2, " : "";
            $address = "$address->line1, $line2$address->city, $state $postalCode, $address->country";
            $h->hotel()->address($address);
        }

        foreach ($data->resort->contact->telephones as $phone) {
            if (isset($phone->number) && !in_array($phone->number, ['@', 'No Fax Capability'])) {
                $h->hotel()->phone($phone->number);
            }
        }

        foreach ($data->resort->contact->faxes as $fax) {
            if (isset($fax->number) && !in_array($fax->number, ['@', 'No Fax Capability'])) {
                $h->hotel()->fax($fax->number);
            }
        }

        if (isset($data->pointsCost) && $data->pointsCost > 0) {
            $h->price()->spentAwards("{$data->pointsCost} Points");
        }

//        $r = $h->addRoom();
//        $r->setType("3 Bedrooms / Partial Kitchen");

        if (isset($data->bedRoom) && is_numeric($data->bedRoom)) {
            $h->booked()->rooms($data->bedRoom);
        }

        $this->logger->debug('Parsed Itinerary:');
        $this->logger->debug(var_export($h->toArray(), true), ['pre' => true]);
    }

    private function parseItinerary($data, $type)
    {
        $this->logger->notice(__METHOD__);
        $h = $this->itinerariesMaster->add()->hotel();
        $this->logger->info("Parse {$type}Itinerary #{$data->Id}", ['Header' => 3]);

        $h->general()
            ->confirmation($data->Id, "RESERVATION NO.")
            ->date2($data->BookingDate);

        $h->general()->status($data->ReservationStatus);

        $h->hotel()
            ->name($data->ResortDisplayName ?? $data->ResortName);

        $h->booked()
            ->guests($data->AdultsReservation)
            ->kids($data->ChildReservation)
            ->checkIn(strtotime($this->http->FindPreg('/^(.+?)T/', false,
                    $data->DateCheckIn) . ', ' . $data->ResortCheckinTime))
            ->checkOut(strtotime($this->http->FindPreg('/^(.+?)T/', false,
                    $data->DateCheckOut) . ', ' . $data->ResortCheckoutTime));

        $headers = [
            'Accept'           => '*/*',
            'Content-Type'     => 'application/json;charset=utf-8',
            'Referer'          => 'https://club.hiltongrandvacations.com/en/reservation',
            "X-Requested-With" => "XMLHttpRequest",
        ];
        $this->http->RetryCount = 0;
        $this->increaseTimeLimit();
        $this->http->GetURL("https://club.hiltongrandvacations.com/en/reservation/item?id={$data->Id}&_=" . date("UB"), $headers);
        $this->http->RetryCount = 1;
        $dataDetail = $this->http->JsonLog(null, 1);

        if (!isset($dataDetail->ItemDetail)) {
            return;
        }

        if (isset($dataDetail->ItemDetail->TaxFee)) {
            $this->sendNotification('check TaxFee // MI');
        }

        if ($data->ReservationStatus == 'Canceled' && $dataDetail->ItemDetail->CancellationNumber) {
            $h->general()->cancellationNumber($dataDetail->ItemDetail->CancellationNumber);
            $h->general()->cancelled();
        }

        if (isset($dataDetail->ItemDetail->StreetCityStateZip)) {
            $h->hotel()
                ->address($dataDetail->ItemDetail->StreetCityStateZip)
                ->phone($dataDetail->ItemDetail->ResortContactPhone);
        }

        if (!empty($dataDetail->ItemDetail->GuestFullName)) {
            $h->general()->traveller(beautifulName($dataDetail->ItemDetail->GuestFullName));
        }
        $spentAwards = 0;

        foreach ($dataDetail->ItemDetail->PointsUsed as $point) {
            $spentAwards += $point->UsedAmount;
        }

        if ($spentAwards > 0) {
            $h->price()->spentAwards($spentAwards . ' points');
        }
        $total = 0;

        foreach ($dataDetail->ItemDetail->PaymentLineItems as $payment) {
            $total += $payment->Amount;
            $h->price()->fee($payment->Description, $payment->Amount);
        }

        if ($total) {
            $h->price()->total($total);

            if ($dataDetail->Item->FormattedCashBookingCost == '$0.00') {
                $h->price()->currency('USD');
            } else {
                $this->sendNotification('check currency // MI');
            }
        }

        $r = $h->addRoom();
        $r->setDescription($dataDetail->ItemDetail->UnitType->Description, false, true);
        $r->setType($dataDetail->ItemDetail->UnitType->Name, false, true);

        $h->booked()->rooms($dataDetail->ItemDetail->UnitType->NumberOfRooms, false, true);

        $this->logger->debug('Parsed Itinerary:');
        $this->logger->debug(var_export($h->toArray(), true), ['pre' => true]);
    }

    private function challengeReCaptchaForm()
    {
        $this->logger->notice(__METHOD__);
        $s = $this->http->FindSingleNode("//form[@id = 'challenge-form']//input[@name = 's']/@value");
        $id = $this->http->FindSingleNode("//form[@id = 'challenge-form']//script/@data-ray");

        if (!$id) {
            return false;
        }
        $key = $this->http->FindSingleNode("//form[@id = 'challenge-form']//script/@data-sitekey");
        $method = 'userrecaptcha';

        if ($this->http->FindSingleNode("//form[@id = 'challenge-form']//input[@name = 'cf_captcha_kind' and @value = 'h']/@value")) {
            $method = "hcaptcha";
        }
        $captcha = $this->parseCaptcha($key, $method);

        if ($captcha == false) {
            return false;
        }
        $this->http->RetryCount = 0;

        if ($s) {
            $s = urlencode($s);
            $this->http->GetURL("https://club.hiltongrandvacations.com/cdn-cgi/l/chk_captcha?s={$s}&id={$id}&g-recaptcha-response={$captcha}#/index");
        } elseif ($method == "hcaptcha") {
            $this->http->SetInputValue("id", $id);
            $this->http->SetInputValue("g-recaptcha-response", $captcha);
            $this->http->SetInputValue("h-captcha-response", $captcha);
            $this->http->PostForm();
        } else {
            $this->http->SetInputValue("g-recaptcha-response", $captcha);
            $this->http->PostForm();
        }
        $this->http->RetryCount = 2;

        return true;
    }

    private function challengeForm()
    {
        $this->logger->notice(__METHOD__);
        $script = $this->http->FindPreg("/setTimeout\(function\(\)\{(.+?)'; 121'/s");

        if (!$script) {
            return false;
        }

        $host = parse_url($this->http->currentUrl(), PHP_URL_HOST);
        $script = str_replace('a.value = ', '', $script);
        $script = str_replace('+ t.length', "+ '{$host}'.length", $script);
        $script = preg_replace("/t = document.createElement\('div'\);.+?getElementById\('challenge-form'\);/s", '', $script);
        // not sure
        $script = "sendResponseToPhp($script)";
        $this->logger->debug($script);

        $jsExecutor = $this->services->get(\AwardWallet\Common\Parsing\JsExecutor::class);
        $encrypted = $jsExecutor->executeString($script);
        $this->logger->debug("encrypted: " . $encrypted);

        sleep(4);
        $params = [];
        $inputs = $this->http->XPath->query("//form[@id='challenge-form']//input");

        for ($n = 0; $n < $inputs->length; $n++) {
            $input = $inputs->item($n);
            $params[$input->getAttribute('name')] = $input->getAttribute('value');

            if ($input->getAttribute('name') == 'jschl_answer') {
                $params[$input->getAttribute('name')] = $encrypted;
            }
        }

        if (!empty($params) && $this->http->FindSingleNode("//form[@id = 'challenge-form' and @method = 'POST']")) {
            $action = $this->http->FindSingleNode("//form[@id='challenge-form']/@action");
            $this->http->NormalizeURL($action);
            $this->http->RetryCount = 0;
            $this->http->GetURL($action . '?' . http_build_query($params));
            $this->http->RetryCount = 2;
        } else {
            $this->http->SetInputValue("jschl_answer", $encrypted);
            $this->http->PostForm();
        }

        return true;
    }

    private function addSubAcc($pointsByYears, $multipleAccounts, $memberNo, $name, $memberSince, $subAccType = "ClubPoints", $clubType = null)
    {
        $this->logger->notice(__METHOD__);
        $this->logger->debug("Total " . count($pointsByYears) . " nodes were found");

        foreach ($pointsByYears as $pointsByYear) {
            $balance = ArrayVal($pointsByYear, 'TotalRescuedDepositedClub', null);
            $year = ArrayVal($pointsByYear, 'Year', null);
            $exp = ArrayVal($pointsByYear, 'ExpireDate', null);

            if (isset($balance, $year)) {
                $this->AddSubAccount(
                    [
                        "DisplayName"       => $multipleAccounts ? "{$clubType} Member #{$memberNo}: {$subAccType} ({$year})" : "{$subAccType} ({$year})",
                        "Code"              => "hiltongvc" . str_replace(' ', '', $clubType) . str_replace(' ', '', $subAccType) . "{$year}#{$memberNo}",
                        "Balance"           => $balance,
                        "ExpirationDate"    => strtotime($exp),
                        "Name"              => $name,
                        "Member"            => $memberNo,
                        "MemberSince"       => $memberSince,
                        "BalanceInTotalSum" => true,
                    ]
                );

                $this->balance += $balance;
            }
        }// foreach ($pointsByYears as $pointsByYear)
    }

